import React, { useState } from "react";

export default function BankLoanApp() { const [phone, setPhone] = useState(""); const [otp, setOtp] = useState(""); const [isLoggedIn, setIsLoggedIn] = useState(false); const [loanType, setLoanType] = useState("Personal"); const [amount, setAmount] = useState(""); const [chat, setChat] = useState(""); const [aiResponse, setAiResponse] = useState("");

const sendOtp = () => { alert(`OTP sent to ${phone}`); };

const verifyOtp = () => { if (otp.length === 6) { setIsLoggedIn(true); } else { alert("Invalid OTP"); } };

const logout = () => { setIsLoggedIn(false); setPhone(""); setOtp(""); };

const applyLoan = () => { alert(`Loan Applied: ${loanType} - ₹${amount}`); };

const askAI = () => { setAiResponse(`AI Response: Based on your request '${chat}', you may be eligible for a ${loanType} loan.`); };

return ( <div className="min-h-screen bg-gray-100 flex items-center justify-center"> <div className="bg-white p-6 rounded-2xl shadow-xl w-full max-w-md"> <h1 className="text-2xl font-bold mb-4 text-center">Bank Loan Portal</h1>

{!isLoggedIn ? (
      <>
      <div className="container-box">
        <input
          type="tel"
          placeholder="Phone Number"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          className="w-full p-2 border rounded mb-2"
        />
        <button onClick={sendOtp} className="w-full bg-blue-600 text-white p-2 rounded mb-2">Send OTP</button>

        <input
          type="text"
          placeholder="Enter OTP"
          value={otp}
          onChange={(e) => setOtp(e.target.value)}
          className="w-full p-2 border rounded mb-2"
        />
        <button onClick={verifyOtp} className="w-full bg-green-600 text-white p-2 rounded">Verify & Login</button>
      </div>
      </>
    ) : (
      <>
      <div className="">
        <p className="mb-3 font-medium">Logged in as: {phone}</p>

        <select
          value={loanType}
          onChange={(e) => setLoanType(e.target.value)}
          className="w-full p-2 border rounded mb-2"
        >
          <option>Personal</option>
          <option>Home</option>
          <option>Education</option>
        </select>

        <input
          type="number"
          placeholder="Loan Amount"
          value={amount}
          onChange={(e) => setAmount(e.target.value)}
          className="w-full p-2 border rounded mb-2"
        />

        <button onClick={applyLoan} className="w-full bg-purple-600 text-white p-2 rounded mb-3">Apply Loan</button>

        <h2 className="font-semibold mb-1">Ask AI Assistant</h2>
        <input
          type="text"
          placeholder="Ask about eligibility, EMI..."
          value={chat}
          onChange={(e) => setChat(e.target.value)}
          className="w-full p-2 border rounded mb-2"
        />
        <button onClick={askAI} className="w-full bg-black text-white p-2 rounded">Ask AI</button>

        {aiResponse && <p className="mt-2 text-sm text-gray-700">{aiResponse}</p>}

        <button onClick={logout} className="w-full mt-4 bg-red-500 text-white p-2 rounded">Logout</button>
      </div>
      </>
    )}
  </div>
</div>

); }
